var bunyan = require('/usr/node/node_modules/bunyan');
var execFile = require('child_process').execFile;
var log = bunyan.createLogger({level: 'debug', name: 'vmxml-driver'});
var getVmobjXML = require('vmxml').getVmobjXML;

execFile('/usr/sbin/zoneadm', ['list', '-c'], function (error, stdout, stderr) {
    var vms = stdout.split(/\n/);
    vms.forEach(function (vm) {
        if (vm.length != 36) {
            return;
        }
        getVmobjXML(vm, {log: log}, function (err, obj) {
            console.log('<boom>');
            console.log(JSON.stringify(obj, null, 2));
            console.log('</boom>');
        });
    });
});
